<?php/* * Smarty plugin * ------------------------------------------------------------- * File:     function.auto_increment.php * Type:     function * Name:     auto_increment * Purpose:  outputs next auto incrementing id * ------------------------------------------------------------- */function smarty_function_auto_increment($params, Dataface_SkinTool $template) {	$app = & Dataface_Application::getInstance();	$query = & $app->getQuery();	$sql = "show table status like '" . $query['-table'] . "'";	$res = mysql_query($sql);	$row = mysql_fetch_array($res);	return $row['Auto_increment'];}?>